mapper.insert:
INSERT INTO CHARACTER_STATE (
    member_id,
    character_type
) VALUES (
    #{memberId},
    #{dto.characterType}
)

mapper.update:
UPDATE
    CHARACTER_ITEM
SET
    item_option_id = #{itemOptionId}
WHERE
    member_id = #{memberId}
AND
    category_id = #{categoryId}

mapper.insert:
INSERT INTO CHARACTER_ITEM (
    category_id,
    member_id,
    item_option_id
) VALUES (
    #{categoryId},
    #{memberId},
    #{itemOptionId}
)
